const int N = 3e5 + 5;
const ll mod = 1e9 + 7;
const ll inf = ll(8e18);

int a[N];

ll binpow (ll a, ll n) {
    if (n == 0) return 1ll;
    if (n % 2) return ((binpow(a, n - 1) % mod) * (a % mod)) % mod;
    else {
        ll b = binpow (a, n / 2ll) % mod;
        return b * b % mod;
    }
}

