ll printDivisors(ll n)
{

    // Note that this loop runs till square root

    for (ll i=2; i<=sqrt(n); i++)
    {

        if (n%i == 0)
          {

            // If divisors are equal, print only one

            if (n/i == i)
             return i;


            else // Otherwise print both
              return n/i;

        }

    }
return 1;

}



