public class GCDL {
	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);

		int t = sc.nextInt();
		while (t-->0) {
			int n = sc.nextInt();
			int[] arr = new int[n];
			for (int i = 0; i < n; i++) arr[i] = sc.nextInt();

			int i = 1, j = n;
			System.out.println(3*n);
			while (i < j) {
				for (int k = 0; k < 3; k++) {
					System.out.println(2 + " " + i + " " + j);
					System.out.println(1 + " " + i + " " + j);

				}
				i++;
				--j;
			}
		}

		sc.close();
	}
}

