    static class CP
    {

        static boolean isPrime(long n)
        {
            if (n <= 1)
                return false;
            if (n == 2 || n == 3)
                return true;
            if (n % 2 == 0 || n % 3 == 0)
                return false;
            for (int i = 5; i * i <= n; i += 6)
            {
                if (n % i == 0 || n % (i + 2) == 0)
                    return false;
            }
            return true;
        }
        static List<Integer> sieve(int size)
        {
            ArrayList<Integer> pr = new ArrayList<Integer>();
            boolean prime[] = new boolean[size];
            for(int i = 2; i < prime.length; i++)  prime[i] = true;
            for(int i = 2; i * i < prime.length; i++)
            {
                if(prime[i])
                {
                    for(int j = i * i; j < prime.length; j += i)
                    {
                        prime[j] = false;
                    }
                }
            }
            for(int i = 2; i < prime.length; i++) if(prime[i]) pr.add(i);
            return pr;
        }

        static long binary_Expo(long a, long b)   // calculating a^b
        {
            long res = 1;
            while (b != 0)
            {
                if ((b & 1) == 1)
                {
                    res *= a;
                    --b;
                }
                a *= a;
                b /= 2;
            }
            return res;
        }

        static long Modular_Expo(long a, long b)
        {
            long res = 1;
            while (b != 0)
            {
                if ((b & 1) == 1)
                {
                    res = (res * a) % mod;
                    --b;
                }
                a = (a * a) % mod;
                b /= 2;
            }
            return res;
        }

        static int i_gcd(int a, int b)  // iterative way to calculate gcd.
        {
            while (true)
            {
                if (b == 0)
                    return a;
                int c = a;
                a = b;
                b = c % b;
            }
        }

        static long gcd(long a, long b)  // here b is the remainder
        {
            if (b == 0)
                return a; //because each time b will divide a.
            return gcd(b, a % b);
        }

        static long ceil_div(long a, long b)   // a numerator b denominator
        {
            return (a + b - 1) / b;
        }

        static int getIthBitFromInt(int bits, int i)
        {
            return (bits >> (i - 1)) & 1;
        }

        static int upper_Bound(int a[], int x)  //closest to the left+1
        {
            int l = -1, r = a.length;
            while (l + 1 < r)
            {
                int m = (l + r) >>> 1;
                if (a[m] <= x)
                    l = m;
                else
                    r = m;
            }
            return l + 1;
        }

        static int lower_Bound(int a[], int x)  //closest to the right
        {
            int l = -1, r = a.length;
            while (l + 1 < r)
            {
                int m = (l + r) >>> 1;
                if (a[m] >= x)
                    r = m;
                else
                    l = m;
            }
            return r;
        }
        static boolean isSquarefactor(int x, int factor, int target)
        {
            int s = (int)Math.round(Math.sqrt(x));
            return factor * s * s == target;
        }

        static boolean isSquare(int x)
        {
            int s = (int)Math.round(Math.sqrt(x));
            return x * x == s;
        }
        static void sort(int a[])
        {
            PriorityQueue<Integer> q = new PriorityQueue<>();
            for (int i = 0; i < a.length; i++)
                q.add(a[i]);
            for (int i = 0; i < a.length; i++)
                a[i] = q.poll();
        }

        static int getMax(int arr[], int n)
        {
            int mx = arr[0];
            for (int i = 1; i < n; i++)
                if (arr[i] > mx)
                    mx = arr[i];
            return mx;
        }
        static int[] z_function(String s)
        {
            int n = s.length(), z[] = new int[n];

            for(int i = 1, l = 0, r = 0; i < n; ++i)
            {
                if(i <= r)
                    z[i] = Math.min(z[i - l], r - i + 1);

                while(i + z[i] < n && s.charAt(z[i]) == s.charAt(i + z[i]))
                    ++z[i];

                if(i + z[i] - 1 > r)
                {
                    l = i;
                    r = i + z[i] - 1;
                }
            }
            return z;
        }
        static int [] kmp(String s)
        {
            int n = s.length(), pi[] = new int[n];
            for(int i = 1; i < n; ++i)
            {
                int j = pi[i - 1];

                while(j > 0 && s.charAt(i) != s.charAt(j))
                    j = pi[j - 1];

                if(s.charAt(i) == s.charAt(j))
                    ++j;

                pi[i] = j;

            }

            return pi;
        }
    }

