class DSU{
	public:
	map<int,int>children;
	std::vector<int>rk,par;
	int N;
	DSU(int n)
	{


		for(int i= 0; i <= n; i++){
			par.push_back(i);
			rk.push_back(0);
		}
		N = n;

	}
	//Return the parent of node x
	int get(int x)
	{
		return x == par[x] ? x : par[x] = get(par[x]);
	}

	//Checks if they both have same parent or not
	bool same_set(int x, int y){
		return (get(x) == get(y));
	}
	void Union(int x, int y)
	{
		x = get(x), y = get(y);
		if(rk[x] > rk[y])
		{
			par[y] = x;
		}
		else if(rk[x] < rk[y])
		{
			par[x] = y;
		}
		else
		{
			par[x] = y;
			rk[y]++;
		}

	}

	//Setting the number of children parent has;
	void setChildren()
	{
		for(int i = 1; i <= N; i++)
		{
			children[get(i)]++;
		}
	}

	//Returns the total number of nodes parent of x is connected to
	//Parent is also included in count
	int get_size(int x){
		return children[get(x)];
	}
};
