int binpow(int a, int b) {
    int res = 1;
    while (b > 0) {
        if (b & 1)
            res = res * a;
        a = a * a;
        b >>= 1;
    }
    return res;
}

int binpow(int a, int b, int mod) {
    int res = 1;
    while (b > 0) {
        if (b & 1)
            res = (res * a)%mod;
        a = (a * a)%mod;
        b >>= 1;
    }
    return res%mod;
}

int gcd(int a, int b) {
    if(b==0) return a;
    return gcd(b,a%b);
}

int lcm (int a, int b) {
    return ((a / gcd(a, b)) * b);
}

int inversemod(int a, int mod) {
    return binpow(a, mod-2, mod);
}

