inline void load() {
  scanf("%d", &n);
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      scanf("%d", &M[i][j]);
    }
  }
}
inline bool check(int x) {
  P[0] = x;
  for (int i = 1; i < n; i++) P[i] = M[0][i] & x;
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      if (i != j && (P[i] & P[j]) != M[i][j]) return false;
    }
  }
  return true;
}
inline void solve() {
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      if (i != j) P[i] |= M[i][j], P[j] |= M[i][j];
    }
  }
  for (int i = 0; i < n; i++) printf("%d ", P[i]);
  printf("\n");
}
