#include <bits/stdc++.h>
using namespace std;
int main() {
  bitset<32> a[110];
  int b[110][110];
  int n;
  scanf("%d", &n);
  for (int i = 1; i <= n; i++)
    for (int j = 1; j <= n; j++) scanf("%d", &b[i][j]);
  for (int i = 1; i <= n; i++) {
    for (int j = 1; j <= n; j++) {
      if (i != j) {
        int t = b[i][j], bit = 0;
        while (t) {
          int res = t % 2;
          t /= 2;
          if (res == 1) {
            a[i].set(bit, res);
            a[j].set(bit, res);
          }
          bit++;
        }
      }
    }
  }
  for (int i = 1; i <= n; i++) {
    printf("%d ", a[i].to_ulong());
  }
  return 0;
}

