class TaskD {

	public void solve(Scanner in, PrintWriter out) {
		int n = in.nextInt();
		int[][] table = new int[n][n];
		for (int i = 0; i < n; ++i)
			for (int j = 0; j < n; ++j)
				table[i][j] = in.nextInt();
		if (n == 1) {
			out.println(0);
			return;
		}
		if (n == 2) {
			out.println(table[0][1] + " " + table[0][1]);
			return;
		}
		int numb = table[0][1];
		for (int i = 2; i < n; ++i)
			numb |= table[0][i];
		out.print(numb);
		for (int i = 1; i < n; ++i) {
			numb = table[i][0];
			for (int j = 1; j < i; ++ j) {
				numb |= table[i][j];
			}
			for (int j = i + 1; j < n; ++j) {
				numb |= table[i][j];
			}
			out.print(" ");
			out.print(numb);
		}
	}

}

