                for (int bit = 0; bit < 30; bit++, val >>= 1) {
                    if ((val & 1) == 1) {
                        ans[i] |= 1 << bit;
                        ans[j] |= 1 << bit;
                    }
                }
            }
        }

        for (int i = 0; i < n; i++)
            out.print(ans[i] + " ");
    }

