        for(int i = 0; i < n; i++) {
            b[i] = readInts();
        }
        int[] a = new int[n];
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < n; j++) {
                if(i != j) {
                    a[i] |= b[i][j];
                    a[j] |= b[i][j];
                }
            }
        }
        for(int i = 0; i < n; i++) {
            writer.write("" + a[i] + " ");
        }
        writer.flush();
    }

    public static void main(String[] args) throws IOException {
        new ProblemD().solve();
