import java.util.*;
public class Main{
    private static final int MAX = 30;
    public static void main(String args[]){
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        sc.nextLine();
        int [][]m = new int[n][n];
        for(int i = 0; i < n; i++){
            for(int j = 0; j < n; j++){
                m[i][j] = sc.nextInt();
            }
            sc.nextLine();
        }
        int a[] = new int[n];
        for(int i = 0; i < n - 1; i++){
            byte[] num1 = crearArray(a[i]);
            int n1 = 0;
            for(int j = i + 1; j < n; j++){
                byte[] num2 = crearArray(a[j]);
                byte[] res = crearArray(m[i][j]);
                calcularDigitos(num1, num2, res);
                n1 = crearInt(num1);
                int n2 = crearInt(num2);
                a[j] = n2;
            }
            a[i] = n1;
        }
        for(int i = 0; i < a.length; i++){
            System.out.print(a[i] + " ");
        }
    }

    public static String mostrarArray(byte[] num){
        StringBuilder sb = new StringBuilder();
        for(int i = num.length - 1; i >= 0; i--){
            sb.append(num[i]);
        }
        return sb.toString();
    }

    public static byte[] crearArray(int num){
        byte[] res = new byte[MAX];
        byte mod;
        int i = 0;
        do{
            mod = (byte)(num % 2);
            res[i] = mod;
            num /= 2;
            i++;
        }while(num > 0);
        return res;
    }

    public static int crearInt(byte[] num){
        int res = 0;
        int k = 1;
        for(int i = 0; i < num.length; i++){
            res += k * num[i];
            k *= 2;
        }
        return res;
    }

    public static void calcularDigitos(byte[] num1, byte[] num2, byte[] res){
        for(int i = 0; i < res.length; i++){
            if(res[i] == 1){
                num1[i] = (byte)1;
                num2[i] = (byte)1;
            }else if(res[i] == 0){
                if(num1[i] == 1 && num2[i] == 1){
                    throw new RuntimeException("Error, no se puede generar los dos números.");
                }
            }else{
                throw new RuntimeException("Error, solamente puede tener 0s o 1s.");
            }
        }
    }
}

