public class D {
    static String to2(int n){
        StringBuffer r=new StringBuffer();

        while(n!=0){
            r.append(n%2);
            n/=2;
        }

        return r.toString();
    }
    public static void main(String[] args) throws NumberFormatException, IOException {
        BufferedReader in=new BufferedReader(new InputStreamReader(System.in));
        PrintWriter out=new PrintWriter(System.out);


        int n=Integer.parseInt(in.readLine());

        int l=to2(1000000000).length();
        char[][] res=new char[n][l];

        for(int i=0; i<n; i++){
            Arrays.fill(res[i],'0');
        }

        for(int i=0; i<n; i++){
            String[] inp=in.readLine().split(" ");
            for(int j=i+1; j<n; j++){
                int cur=Integer.parseInt(inp[j]);
                String t2=to2(cur);
                int z=l-1;
                int k=0;
                while(k<t2.length()){
                    if(t2.charAt(k)=='1'){
                        res[i][z]='1';
                        res[j][z]='1';
                    }
                    k++;
                    z--;
                }
            }
        }


        for(int i=0; i<n; i++){
            out.print(new BigInteger(new String(res[i]),2)+" ");
        }


        out.close();
    }
