int main() {
  cin >> n;
  for (int i = (0); i < (n); ++i)
    for (int j = (0); j < (n); ++j) {
      int t;
      cin >> t;
      if (t == -1) continue;
      for (int k = (0); k < (32); ++k) B[i][j][k] = ((t & (1 << k)) != 0);
    }
  if (n == 1) {
    cout << 0 << endl;
    return 0;
  }
  for (int bit = (0); bit < (32); ++bit) {
    bool was = false;
    for (int i = (0); i < (n); ++i) {
      bool all0 = true, all1 = true;
      for (int j = (0); j < (n); ++j) {
        if (i == j) continue;
        if (B[i][j][bit] == 0)
          all1 = false;
        else
          all0 = false;
      }
      if (all1) {
        for (int j = (0); j < (n); ++j) R[j][bit] = true;
        was = true;
        break;
      } else if (!all0 && !all1) {
        R[i][bit] = true;
        for (int j = (0); j < (n); ++j)
          if (i != j) {
            if (B[i][j][bit] == 1)
              R[j][bit] = 1;
            else
              R[j][bit] = 0;
          }
        was = true;
        break;
      }
    }
  }
  for (int i = (0); i < (n); ++i) {
