int table[105][105];
unsigned int a[105];
unsigned int power2[32];
inline void set(int ind, int pos) { a[ind] = a[ind] | power2[pos]; }
inline bool is_set(int i, int j, int pos) {
  if (table[i][j] & power2[pos])
    return true;
  else
    return false;
}
void calc() {
  int i;
  power2[0] = 1;
  for (i = 1; i < 32; i++) {
    power2[i] = 2 * power2[i - 1];
  }
}
int main() {
  int n, i, j, k;
  scanf("%d", &n);
  calc();
  for (i = 0; i < n; i++) {
    for (j = 0; j < n; j++) {
      scanf("%d", &table[i][j]);
    }
  }
  for (i = 0; i < n; i++) a[i] = 0;
  for (i = 0; i < n; i++) {
    for (j = i + 1; j < n; j++) {
      for (k = 0; k < 32; k++) {
        if (is_set(i, j, k)) {
          set(i, k);
          set(j, k);
        }
      }
    }
  }
  for (i = 0; i < n; i++) {
    printf("%d ", a[i]);
  }
}

