        int n = r.nextInt();

        int[][] b = new int[n][n];

        for (int i = 0; i < n; i++) for (int j = 0; j < n; j++) b[i][j] = r.nextInt();

        int[] res = new int[n];

        for (int i = 0; i < n; i++) for (int j = 0; j < n; j++) {
