public class Solution {

	public void solve(){
		Scanner sc = new Scanner(System.in);
		int N = sc.nextInt();
		int[][] a = new int[N][30];

		for(int i = 0; i < N; i++){
			for(int j = 0; j < N; j++){
				int num = sc.nextInt();

				if( i != j){
					String numStr = Integer.toBinaryString(num);
					for(int k = 0; k < numStr.length(); k++){
						if(numStr.charAt( numStr.length()-k-1 ) == '1'){
							a[i][k] = 1;
							a[j][k] = 1;
						}
					}
				}
			}
		}

		for(int i = 0; i < N; i++){
			int total = 0;
			for(int j = 0; j < 30; j++){
				if(a[i][j] == 1)
					total += Math.pow(2, j);
				//System.out.print(a[i][j] + " ");
			}
			System.out.print(total);
			if(i < N-1) System.out.print(" ");
		}
	}

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		new Solution().solve();
	}

}

