    public static boolean isPalind(int i, int j) {
        while (i < j) {
            if (line.charAt(i) != line.charAt(j)) {
                return false;
            }
            i++;
            j--;
        }
        return true;

    }

    static long count(long val, ArrayList<Integer> list) {
        int index = 0;
        long prime = list.get(0);
        long store = val;
        while (val >= prime * prime) {
            if (val % prime == 0) {
                int count = 0;
                while (val % prime == 0) {
                    val /= prime;
                    count++;
                }
                store /= pow(prime, count - (count % 2));
            }
            prime = list.get(++index);
        }
        return store;
    }

    public static class FT {

        int[] data;

        FT(int n) {
            data = new int[n];
        }

        public void update(int index, int value) {
            while (index < data.length) {
                data[index] += value;
                index += (index & (-index));
            }
        }

        public int get(int index) {
            int result = 0;
            while (index > 0) {
                result += data[index];
                index -= (index & (-index));
            }
            return result;

        }
    }

    public static long gcd(long a, long b) {
        if (b == 0) {
            return a;
        }
        return gcd(b, a % b);
    }

    public static long pow(long a, long b) {
        if (b == 0) {
            return 1;
        }
        if (b == 1) {
            return a;
        }
        long val = pow(a, b / 2);
        if (b % 2 == 0) {
            return val * val;
        } else {
            return val * (val * a);
        }
    }

