    public void run() throws Exception {
        //in = new BufferedReader(new FileReader("D.IN"));
        in = new BufferedReader(new InputStreamReader(System.in));
        out = new PrintWriter(System.out);

        int n = nextInt();
        int[][] b = new int[n][n];
        int[][] c = new int[n][40];
        int[] max = new int[n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                b[i][j] = nextInt();
                if (i != j) {
                    int x = b[i][j];
                    int index = 0;
                    while (x > 0) {
                        int d = x % 2;
                        c[i][index] = Math.max(d, c[i][index]);
                        index++;
                        x /= 2;
                    }
                    if (index > max[i]) {
                        max[i] = index;
                    }
                }
            }
        }
        int[] a = new int[n];
        String[] bin = new String[n];
        for (int i = 0; i < n; i++) {
            bin[i] = "";
            for (int j = max[i]; j >= 0; j--) {
                bin[i] += c[i][j] + "";
            }
        }
        for (int i = 0; i < n; i++) {
            a[i] = Integer.parseInt(bin[i], 2);
        }

        for (int i = 0; i < n; i++) {
            out.print(a[i] + " ");
        }
        out.println();
        out.flush();
        out.close();
        in.close();
    }

    public static void main(String[] args) throws Exception {
        new Example().run();
    }
}

