import java.util.*;
import java.io.*;

public class d {

	String input = "";
	String output = "";

	FastScanner in;
	PrintWriter out;

	void solve() throws Exception {
		int n = in.nextInt();
		int a[] = new int[n];
		int b[][] = new int[n][n];
		for (int i = 0; i<n; i++){
			for (int j = 0; j<n; j++)
				b[i][j] = in.nextInt();
		}
		int t[] = new int[30];
		for (int i = 0; i<n; i++){
			for (int j = 0; j<30; j++) t[j] = 0;
			for (int j = 0; j<n; j++){
				if (i == j) continue;
				int l = 0;
				int m = b[i][j];
				while (m > 0){
					if (m % 2 == 1 && t[l] == 0) t[l] = 1;
					m /= 2;
					l++;
				}
			}
			int x = 0;
			for (int j = 0; j<30; j++)
				x += Math.pow(2, j) * t[j];
			a[i] = x;
		}
		for (int i = 0; i<n; i++)
			out.print(a[i] + " ");

	}

	void run() {
		try {
			if (input.length() == 0) {
				InputStreamReader ins = new InputStreamReader(System.in);
				in = new FastScanner(new BufferedReader(ins));
			} else {
				FileReader f = new FileReader(input);
				in = new FastScanner(new BufferedReader(f));
			}
			if (output.length() == 0) {
				out = new PrintWriter(System.out);
			} else
				out = new PrintWriter(new File(output));

			solve();

			out.flush();
			out.close();
		} catch (Exception ex) {
			ex.printStackTrace();
			out.close();
		}
	}

	public static void main(String args[]) {
		new d().run();
	}

	class FastScanner {
		BufferedReader bf;
		StringTokenizer st;

		FastScanner(BufferedReader bf) {
			this.bf = bf;
		}

		public String next() throws IOException {
			while (st == null || !st.hasMoreTokens())
				st = new StringTokenizer(bf.readLine());

			return st.nextToken();
		}

		public int nextInt() throws IOException {
			return Integer.parseInt(next());
		}

		public long nextLong() throws IOException {
			return Long.parseLong(next());
		}

		public double nextDouble() throws IOException {
			return Double.parseDouble(next());
		}

	}

}

