public class Dcroc {
	static Scanner in = new Scanner(System.in);
	static PrintWriter w = new PrintWriter(System.out, true);

	static int ni() {
		return in.nextInt();
	}

	static String nl() {
		return in.nextLine();
	}

	static void pl(int v) {
		w.println(v);
	}

	static void pl(String s) {
		w.println(s);
	}

	public static void main(String[] args) {
		int n = ni();
		int[][] b = new int[n][n];
		for (int i = 0; i < n; i++) {
			for (int j = 0; j < n; j++) {
				b[i][j] = ni();
			}
		}
		int[][] nums = new int[n][32]; 
		outer: for (int bi = 0; bi < 32; bi++) {
			for (int i = 0; i < n; i++) {
				for (int j = i+1; j < n; j++) {
					if (((b[i][j] >>> bi) & 1) == 1) {
						nums[i][bi] = 1;
						nums[j][bi] = 1;
						for (int k = 0; k < n; k++) {
							if (k == i) continue;
							if (k == j) continue;
							if (((b[k][i] >>> bi) & 1) == 1) {
								nums[k][bi] = 1;
							} else {
								nums[k][bi] = 0;
							}
						}
						continue outer;
					}
				}
			}
		}
		StringBuilder sb = new StringBuilder();
		for (int i = 0; i < n; i++) {
			int res = 0; 
			for (int bi = 0; bi < 32; bi++) {
				if (nums[i][bi] == 1) {
					res += (1 << bi);
				}
			}
			if (res > 1000000000) {
				res %= 1000000000;
			}
			sb.append(res).append(" ");
		}
		pl(sb.toString());
	}
}

