int a;
long long binpow(long long a, long long b, long long m) {
  if (a == 0) return a;
  if (b == 0) return 1;
  if (b == 1) return a % m;
  long long temp = binpow(a, b / 2, m) % m;
  if (b & 1) return (a * temp * temp) % m;
  return (temp * temp) % m;
}
int k = -1;
void dfs(int i, int p) {
  if (vis[i]) {
    k = i;
