  }
  vis[i] = 1;
  for (auto j : e[i]) {
    if (j != p) {
      dfs(j, i);
    }
  }
  return;
}
void dfsi(int i, int p, int h) {
  if (visi[i]) return;
  visi[i] = 1;
  pr[i] = p;
  for (auto j : e[i]) {
    if (j != p) {
      if (j == h) {
        pr[j] = i;
        return;
      }
      dfsi(j, i, h);
    }
  }
  return;
}
long long nck(long long n, long long k) {
  long long g = 1;
  map<long long, long long> m;
  for (long long i = n - k + 1; i <= n; i++) {
    g *= i;
    for (long long j = 2; j <= k; j++) {
      if (m[j] == 0 && g % j == 0) {
        g /= j;
        m[j]++;
      }
    }
  }
  return g;
}
