int n;
int C[100005], a[100005];
inline int lowbit(int x) { return x & -x; }
void update(int x, int val) {
  while (x <= n) {
    C[x] += val;
    x += lowbit(x);
  }
}
int sum(int x) {
  int res = 0;
  while (x > 0) {
    res += C[x];
    x -= lowbit(x);
  }
  return res;
}
