long long check(long long n, long long a[], long long key) {
  long long s = 0, j;
  long long e = n - 1;
  while (s <= e) {
    long long mid = (s + e) / 2;
    if (a[mid] == key) {
      if (a[mid] != a[mid + 1])
        return mid;
      else {
        j = mid + 1;
        while (a[mid] == a[j] && j <= n - 1) {
          j++;
        }
        return j - 1;
      }
    }
    if (a[mid] > key) {
      if (mid > 0) {
        if (a[mid - 1] <= key)
          return mid - 1;
        else
          e = mid - 1;
      } else {
        return -1;
      }
    } else if (a[mid] < key) {
      if (mid < n - 1) {
        if (a[mid + 1] > key)
          return mid;
        else if (a[mid + 1] == key)
          return mid + 1;
        else
          s = mid + 1;
      } else {
        return n - 1;
      }
    }
  }
  return -1;
}
