class TaskB {
    int[] aib;
    int N;

    public void solve(int testNumber, InputReader in, PrintWriter out) {
        N = in.nextInt();
        aib = new int[N + 1];
        int[] A = new int[N];

        for(int i = 0; i < N; i++)
            A[i] = in.nextInt();

        int ans = 0;
        for(int i = N - 1; i >= 0; i--) {
            if(query(A[i]) > 0) {
                ans = i + 1;
                break;
            }
            update(A[i]);
        }

        out.println(ans);
    }

    private void update(int pos) {
        while(pos <= N) {
            aib[pos]++;
            pos += (pos & (-pos));
        }
    }

    private int query(int pos) {
        while(pos > 0) {
            if(aib[pos] > 0)
                return 1;
            pos -= (pos & (-pos));
        }
        return 0;
    }
}

class InputReader {
    private InputStream stream;
    private byte[] buf = new byte[1024];
    private int curChar;
    private int numChars;

    public InputReader(InputStream stream) {
        this.stream = stream;
    }

    public int read() {
        if (numChars == -1)
            throw new InputMismatchException();
        if (curChar >= numChars) {
            curChar = 0;
            try {
                numChars = stream.read(buf);
            } catch (IOException e) {
                throw new InputMismatchException();
            }
            if (numChars <= 0)
                return -1;
        }
        return buf[curChar++];
    }

    public int nextInt() {
        return Integer.parseInt(nextString());
    }

    public String nextString() {
        int c = read();
        while (isSpaceChar(c))
            c = read();
        StringBuffer res = new StringBuffer();
        do {
            res.appendCodePoint(c);
            c = read();
        } while (!isSpaceChar(c));
        return res.toString();
    }

    private boolean isSpaceChar(int c) {
        return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
    }

    }

