int binarySearch(int a[], int n, long long x) {
  if (x < a[1]) return 0;
  if (x >= a[n]) return n;
  int i = 1, j = n, mid = 0;
  while (j - i > 1) {
    mid = (i + j) / 2;
    if (a[mid] > x)
      j = mid;
    else
      i = mid;
  }
  return (a[j] > x ? i : j);
}
long long combination(int c, int i) {
  if (i == 0)
    return 1;
  else if (i == 1)
    return c;
  else
    return ((c - i + 1) * combination(c, i - 1)) / i;
}
