int LPS(char *str) {
  int n = strlen(str);
  int i, j, cl;
  int l[n][n];
  for (i = 0; i < n; i++) l[i][i] = 1;
  for (cl = 2; cl <= n; cl++) {
    for (i = 0; i < n - cl + 1; i++) {
      j = i + cl - 1;
      if (str[i] == str[j] && cl == 2)
        l[i][j] = 2;
      else if (str[i] == str[j])
        l[i][j] = l[i + 1][j - 1] + 2;
      else
        l[i][j] = max(l[i][j - 1], l[i + 1][j]);
    }
  }
  return l[0][n - 1];
}
