    private void run() throws IOException {
        MyScanner sc = new MyScanner(System.in);
        int n = sc.nextInt();
        int min = 1, count = 0;
        int susp = 0;
        boolean[] was = new boolean[n + 1];
        for (int i = 0; i < n; i++) {
            int a = sc.nextInt();
            if (a > min) {
                count += 1 + susp;
                susp = 0;
            } else {
                ++susp;
            }
            was[a] = true;
            if (a == min) {
                ++min;
                while (min < n && was[min]) {
                    ++min;
                }
            }
        }
        System.out.println(count);
    }

    public static void main(String[] args) throws IOException {
        Codeforces cf = new Codeforces();
        cf.run();
    }
