class TaskB {
    public void solve(int testNumber, Scanner in, PrintWriter out) {
	    int n = in.nextInt();
	    int[] a = IOUtils.readIntArray(in, n);
	    int res = 0;
	    int min = a[n - 1];
	    for (int i = n - 2; i >= 0; --i) {
		    if (a[i] > min) {
			    res = i + 1;
			    break;
		    } else {
			    min = a[i];
		    }
	    }
	    out.print(res);
    }
}

