        for(int i =0; i < n; i++)
            c[i] = b[i];
        for(int i = 1; i < n; i++)
            b[i] = Math.max(b[i], b[i - 1]);
        TreeSet<Integer> used = new TreeSet<Integer>();
        TreeSet<Integer> us = new TreeSet<Integer>();

        int ans = 0;
        for(int i = 1; i < n; i++){
