import java.io.*;
import java.util.*;

public class B
{
    InputReader236 in;
    PrintWriter out;

    void run() throws IOException
    {
        in = new InputReader236(System.in);
        out = new PrintWriter(new OutputStreamWriter(System.out));
        solve();
        out.flush();
    }

    public static void main(String[] args) throws IOException
    {
        new B().run();
    }

    void solve() throws IOException
    {
        int n = in.readInt();

        int old[] = new int[n];
        boolean newMsg[] = new boolean[n];
        for (int i = 1; i <= n; i++)
        {
            int pos = in.readInt();
            old[pos - 1] = i;
            newMsg[i - 1] = false;
        }
        // out.println(Arrays.toString(old));
        int maxYet = old[0];
        int maxUpd = -1;
        int cnt = 0;
        for (int i = 1; i < n; i++)
        {
            if (old[i] < maxYet)
            {
                cnt++;
                newMsg[i] = true;
                maxUpd = Math.max(maxUpd, old[i]);
            }
            maxYet = Math.max(maxYet, old[i]);
        }
        //out.println(maxUpd);
        if (cnt != 0)
            for (int i = 0; i < n; i++)
            {
                if (!newMsg[i] && old[i] < maxUpd)
                    cnt++;
            }
        out.println(cnt);

    }
}

class InputReader236
{
    private InputStream stream;
    private byte[] buf = new byte[1024];
    private int curChar;
    private int numChars;

    public InputReader236(InputStream stream)
    {
        this.stream = stream;
    }

    public int read()
    {
        if (numChars == -1)
            throw new InputMismatchException();
        if (curChar >= numChars)
        {
            curChar = 0;
            try
            {
                numChars = stream.read(buf);
            }
            catch (IOException e)
            {
                throw new InputMismatchException();
            }
            if (numChars <= 0)
                return -1;
        }
        return buf[curChar++];
    }

    public int readInt()
    {
        int c = read();
        while (isSpaceChar(c))
            c = read();
        int sgn = 1;
        if (c == '-')
        {
            sgn = -1;
            c = read();
        }
        int res = 0;
        do
        {
            if (c < '0' || c > '9')
                throw new InputMismatchException();
            res *= 10;
            res += c - '0';
            c = read();
        } while (!isSpaceChar(c));
        return res * sgn;
    }

    public String readString()
    {
        int c = read();
        while (isSpaceChar(c))
            c = read();
        StringBuffer res = new StringBuffer();
        do
        {
            res.appendCodePoint(c);
            c = read();
        } while (!isSpaceChar(c));
        return res.toString();
    }

    public Long readLong()
    {
        return Long.parseLong(readString());
    }

    public Double readDouble()
    {
        return Double.parseDouble(readString());
    }

    public static boolean isSpaceChar(int c)
    {
        return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
    }
}

