import java.math.*;

public class Div2Prob1 {
    public static void main(String[] args)
        throws Exception {
        new Div2Prob1().start();
    }

    public void start()
        throws Exception {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        System.out.println(calc(br));
    }

    int calc(BufferedReader br) throws IOException {
        br.readLine();
        String[] split = br.readLine().split(" ");
        int t = 15;
        int idx = 0;
        while (true) {
            if (idx == split.length) return t;
            int ist = Integer.parseInt(split[idx++]);
            if (ist > t) return t;
            t = Math.min(90, ist + 15);
        }
    }
}

