            minutes[i] = scanner.nextInt();
        }
        if (n == 1) {
            if (minutes[0] > 15)
                System.out.printf("%d", 15);
            else
                System.out.printf("%d", minutes[0] + 15);
        } else {
            if (minutes[0] > 15)
                System.out.printf("%d", 15);
            else {
                boolean flag = false;
                for (int i = 0; i < n - 1; ++i) {
                    int sub = minutes[i + 1] - minutes[i];
                    if (sub > 15) {
                        flag = true;
                        System.out.printf("%d", minutes[i] + 15);
                        break;
                    }
                }
                if (!flag)
                    System.out.printf("%d", minutes[n - 1] + 15 >= 90 ? 90 : minutes[n - 1] + 15);
            }
        }
    }

}

