def count_time(list_minutes,initial=0):
    output=0
    if list_minutes==[]:
        if initial in range(75,90):
            return 90-initial
        elif initial == 90:
            return 0
        else:
            return 15

    current = list_minutes[0]
    interval = abs(current-initial)

    if interval > 15:
        return 15
    else:
        return interval + count_time(list_minutes[1:],current)


