public class BearAndGame {
    static int watchingTvMininues() {
        Scanner obj = new Scanner(System.in);
        int number = obj.nextInt();
        int idx = 0;
        int sum = 0;
        int nextBoringTimes = 15;
        int m = 0;
        while (idx < number) {
            m = obj.nextInt();
            if ( nextBoringTimes < m) {
                return nextBoringTimes;
            } else {
                nextBoringTimes = m + 15;
            }

            idx++;            
        }

        return (90 < nextBoringTimes) ? 90 : nextBoringTimes;
    }
    public static void main(String argrs[]) {
        System.out.println(watchingTvMininues());

    }

}

