long long power(long long aa, long long pp) {
  long long xx = 1, yy = aa % 1000000007;
  while (pp > 0) {
    if (pp & 1) xx = (xx * yy) % 1000000007;
    yy = (yy * yy) % 1000000007;
    pp = pp >> 1;
  }
  return xx % 1000000007;
}
