public class BearGame {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        PrintWriter writer = new PrintWriter(System.out);
        int n = scanner.nextInt();
        int[] minutes = new int[n+1];
        for (int i=0;i<=n-1;++i) {
            minutes[i] = scanner.nextInt();
        }
        minutes[minutes.length-1]=90;
        int watchTime=0;
        int prev = 1;
        for(int i=0;i<=minutes.length-1;++i){
            if(((minutes[i]-1)-prev)+1 >=15){
                watchTime=prev+14;
                break;
            }
            prev = minutes[i]+1;
            watchTime=minutes[i];
        }
        writer.print(watchTime);
        writer.flush();
        writer.close();
        scanner.close();
    }
}

