        PrintWriter out = new PrintWriter(outputStream);
        ABearAndGame solver = new ABearAndGame();
        solver.solve(1, in, out);
        out.close();
    }

    static class ABearAndGame {
        public void solve(int tt, FastScanner fs, PrintWriter out) {
            int n = fs.nextInt();
            int[] t = new int[n + 2];
            t[0] = 0;
            t[n + 1] = 90;
            for (int i = 1; i <= n; i++) t[i] = fs.nextInt();
            for (int i = 1; i <= n + 1; i++) {
                if (t[i] - t[i - 1] > 15) {
                    out.println(t[i - 1] + 15);
                    return;
                }
            }
            out.println(90);
        }

    }

    static class FastScanner {
        BufferedReader br;
        StringTokenizer st;

        public FastScanner(InputStream inputStream) {
            br = new BufferedReader(new InputStreamReader(inputStream));
        }

        public String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(next());
        }

    }
}

