public class BearGame {
    void solve() {
        int n = in.nextInt();
        int[] t = new int[n];
        for (int i = 0; i < n; i++) t[i] = in.nextInt();

        int ans = 0;
        if (t[0] > 15) {
            out.println(ans + 15);
            return;
        }
        ans = t[0];
        for (int i = 1; i < n; i++) {
            if (t[i] - t[i - 1] > 15) {
                out.println(ans + 15);
                return;
            }
            ans = t[i];
        }
        ans += Math.min(15, 90 - t[n - 1]);
        out.println(ans);
    }

    public static void main(String[] args) {
        in = new FastScanner(new BufferedReader(new InputStreamReader(System.in)));
        out = new PrintWriter(System.out);
        new BearGame().solve();
        out.close();
    }

