		System.out.println(getTimeLength(a));
	}

	static int TIMEOUT = 15; 
	static int FULLTIME = 90;
	static int getTimeLength(int[] arr) {
		int result = 0; 
		result = arr[0];
		if (result > TIMEOUT) return TIMEOUT;

		for ( int i = 1; i < arr.length; i++) {
			int length = arr[i] - arr[i-1]; 
			if (length > TIMEOUT) return result + TIMEOUT;
			result += length;
		}

if (FULLTIME - result < TIMEOUT) return FULLTIME;
		else result += TIMEOUT;
		return result;
	}
}

