    public static int countNumberOfMinute(String input1, String input2) {
        int numberOfInterestingMin = Integer.valueOf(input1);
        int watchingMinute = 0;
        String [] interestingMinutes = input2.split(" ");
        if(interestingMinutes.length == 1 && Integer.valueOf(interestingMinutes[0]) <= 15 ) {
            return Integer.valueOf(interestingMinutes[0]) + 15;
        } else {
            if (Integer.valueOf(interestingMinutes[0]) > 15) {
                return 15;
            } else {
                for (int i = 0; i < interestingMinutes.length - 1; i++) {
                    int firstNum = Integer.valueOf(interestingMinutes[i]);
                    int secondNum = Integer.valueOf(interestingMinutes[i + 1]);
                    if (secondNum - firstNum > 15) {
                        return  firstNum + 15;
                    }

                    if (secondNum == Integer.valueOf(interestingMinutes[interestingMinutes.length - 1]) && secondNum >= 75 && watchingMinute == 0) {
                        return  90;
                    }
                    if (i+1 == interestingMinutes.length - 1 && secondNum <= 75 && watchingMinute == 0) {
                        return secondNum + 15;
                    }
                }
            }
        }
        return 0;

    }
}

