	public static void solve(MyScanner sc, PrintWriter out) {

		int n = sc.nextInt();	// 1 <= n <= 90
		int[] bf = new int[n];	

		for(int i=0; i < n; i++) {
			bf[i] = sc.nextInt();
		}

		if (n == 1) {
			if(bf[0] > 15) {
				out.print(15);
				return;
			}
			else {
				out.print(bf[0]+15);
				return;
			}
		}
		else {

			if(bf[0] > 0 ) {
				if(bf[0] > 15) {
					out.print(15);
					return;
				}
			}

			for(int i = 0; i < (n-1); i++) {
				if((bf[i+1] - bf[i]) > 15) {
					out.print(bf[i]+15);
					return;
				}
			}

			if (bf[n-1] + 15 > 90) {
				out.print(90);
				return;
			}
			else {
				out.print(bf[n-1]+15);
				return;
			}
		}		
	}

	public static PrintWriter out;

