	}

	private static int ints() throws IOException {
		return Integer.parseInt(br.readLine());
	}

	private static int[] ints(int N) throws IOException {
		st = new StringTokenizer(br.readLine());
		int[] nums = new int[N];
		for(int i = 0; i < N; i += 1) {
			nums[i] = Integer.parseInt(st.nextToken());
		}
		return nums;
	}

	public static void main(String[] args) throws IOException {
	    int N = ints();
	    int[] nums = ints(N);
	    int[] t = new int[N + 2];
	    for(int i = 1; i <= N; i += 1) {
	        t[i] = nums[i - 1];
	    }
	    t[N + 1] = 91;
		Solution obj = new Solution();
	    System.out.println(obj.watchingTime(t, N + 1));
		br.close();
    }
}

class Solution {
    public int watchingTime(int[] nums, int N) {
        int time = 0;
        for(int i = 1; i <= N; i += 1) {
            if(nums[i] - time > 15) {
                return (time + 15);
            }
            time = nums[i];
        }

        return 90;
    }
}

