class TaskB {


    public void solve(int testNumber, InputReader in, PrintWriter out) {
        Integer minutes = in.nextInt();
        Integer results = 0;
        List<Integer> interesting = new ArrayList<>();
        interesting.add(0);
        for(int i=0;i<minutes;i++){
            interesting.add(in.nextInt());
        }
        interesting.add(90);

        int sum = 0;

        for(int i=1;i<interesting.size();i++){
            int last = interesting.get(i-1);
            int current = interesting.get(i);

            if(current - last > 15){
                results = sum+15 > results ? sum+15 : results;
                break;
            }
            else{
                sum+=current-last;
                if(sum==90){
                    results = 90;
                }
            }
        }

        out.println(results);


    }



}







class InputReader {
    public BufferedReader reader;
    public StringTokenizer tokenizer;

    public InputReader(InputStream stream) {
        reader = new BufferedReader(new InputStreamReader(stream), 32768);
        tokenizer = null;
    }

    public String next() {
        while (tokenizer == null || !tokenizer.hasMoreTokens()) {
            try {
                tokenizer = new StringTokenizer(reader.readLine());
            } catch (IOException e) {
                throw new RuntimeException(e);
            }
        }
        return tokenizer.nextToken();
    }

    public String nextLine(){
        try {
            return reader.readLine();
        } catch (IOException e){
            throw new RuntimeException(e);
        }
    }

    public int nextInt() {
        return Integer.parseInt(next());
    }
    public long nextLong() { return Long.parseLong(next()); }

}



class Pair<F, S> {
    public final F first;
    public final S second;




    public Pair(F first, S second) {
        this.first = first;
        this.second = second;
    }

    @Override
    public boolean equals(Object o) {
        if (!(o instanceof Pair)) {
            return false;
        }
        Pair<?, ?> p = (Pair<?, ?>) o;
        return Objects.equals(p.first, first) && Objects.equals(p.second, second);
    }

    @Override
    public int hashCode() {
        return (first == null ? 0 : first.hashCode()) ^ (second == null ? 0 : second.hashCode());
    }

    @Override
    public String toString() {
        return "(" + first + ", " + second + ')';
    }
}

