public class AnatolyAndCockroaches {
	public static BufferedReader f = new BufferedReader(new InputStreamReader(System.in));
	public static StringTokenizer st;

	public static void main(String[] args) throws IOException {
		int n = nextInt();
		String s = nextString();

		int x = 0;
		int y = 0;
		for (int i = 0; i < n; i++) {
			char c = s.charAt(i);
			if (i % 2 == 0)
				x += c == 'r' ? 1 : 0;
			else
				y += c == 'b' ? 1 : 0;
		}

		int min = Integer.MAX_VALUE;
		min = Math.max(x, y);

		x = (n+1)/2 - x;
		y = n/2 - y;

		min = Math.min(min, Math.max(x, y));
		System.out.println(min);
	}

	public static String nextLine() throws IOException {
		return f.readLine();
	}

	public static String nextString() throws IOException {
		while (st == null || !st.hasMoreTokens())
			st = new StringTokenizer(f.readLine());
		return st.nextToken();
	}

	public static int nextInt() throws IOException {
		return Integer.parseInt(nextString());
	}

	public static long nextLong() throws IOException {
		return Long.parseLong(nextString());
	}

	public static int[] intArray(int n) throws IOException {
		int[] a = new int[n];
		for (int i = 0; i < n; i++)
			a[i] = nextInt();
		return a;
	}

	public static long[] longArray(int n) throws IOException {
		long[] a = new long[n];
		for (int i = 0; i < n; i++)
			a[i] = nextLong();
		return a;
	}
}

