class Answer {
    private final int INF = (int) (1e9 + 7);

    private int minstart(int[] a, int type) {
        int x = 0;
        int y = 0;
        int b = type;
        for (int i = 0; i < a.length; i++) {
            if (b != a[i]) {
                if (b == 1) {
                    y++;
                } else {
                    x++;
                }
            }
            b ^= 1;
        }
        return Math.max(x, y);
    }

    public void solve(InputReader in, PrintWriter out) throws IOException {
        int n = in.nextInt();
        String s = in.next();

        if (n == 1) {
            out.print("0");
            return;
        }

        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = (s.charAt(i) == 'b') ? 0 : 1;
        }

        int ans = Math.min(minstart(a, 0), minstart(a, 1));
        out.print(ans);
    }
}

