  cout << endl;
}
int Convert(int p[], int q[]) {
  int sump = 0, sumq = 0;
  for (int i = 1; i <= n; i++) sump += p[i], sumq += q[i];
  int ans = mod(sumq - sump);
  for (int i = 1; i <= n; i++) {
    if (sump < sumq)
      if (p[i] == 0 && q[i] == 1) {
        p[i] = 1;
        sump++;
      }
    if (sump > sumq)
      if (p[i] == 1 && q[i] == 0) {
        p[i] = 0;
        sump--;
      }
  }
  for (int i = 1; i <= n; i++) {
    if (sump < sumq)
      if (p[i] == 0) p[i] = 1, sump++;
    if (sump > sumq)
      if (p[i] == 1) p[i] = 0, sump--;
  }
  int hdist = 0;
  for (int i = 1; i <= n; i++)
    if (p[i] != q[i]) hdist++;
  ans = ans + hdist / 2;
  return ans;
}
