import java.io.*;
import java.util.*;

public class B {
    public static void solution(BufferedReader reader, PrintWriter out)
            throws IOException {
        In in = new In(reader);
        int n = in.nextInt();
        char[] c = in.next().toCharArray();
        int ans1 = solve(n, c, new char[] { 'r', 'b' });
        int ans2 = solve(n, c, new char[] { 'b', 'r' });
        out.println(Math.min(ans1, ans2));
    }

    private static int solve(int n, char[] c, char[] m) {
        int[] cnt = new int[2];
        for (int i = 0; i < n; i++)
            if (c[i] != m[i & 1])
                cnt[i & 1]++;
        return Math.max(cnt[0], cnt[1]);
    }

    public static void main(String[] args) throws Exception {
        BufferedReader reader = new BufferedReader(
                new InputStreamReader(System.in));
        PrintWriter out = new PrintWriter(
                new BufferedWriter(new OutputStreamWriter(System.out)));
        solution(reader, out);
        out.close();
    }

    protected static class In {
        private BufferedReader reader;
        private StringTokenizer tokenizer = new StringTokenizer("");

        public In(BufferedReader reader) {
            this.reader = reader;
        }

        public String next() throws IOException {
            while (!tokenizer.hasMoreTokens())
                tokenizer = new StringTokenizer(reader.readLine());
            return tokenizer.nextToken();
        }

        public int nextInt() throws IOException {
            return Integer.parseInt(next());
        }

        public double nextDouble() throws IOException {
            return Double.parseDouble(next());
        }

        public long nextLong() throws IOException {
            return Long.parseLong(next());
        }
    }
}

