/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 *
 * @author AlexFetisov
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskB_d2_cf373 solver = new TaskB_d2_cf373();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskB_d2_cf373 {
        public void solve(int testNumber, InputReader in, PrintWriter out) {
            int n = in.nextInt();
            int[] a = new int[n];
            int[] b = new int[n];
            char[] c = in.nextString().toCharArray();
            for (int i = 0; i < n; ++i) {
                a[i] = c[i] == 'r' ? 1 : 0;
                b[i] = 1 - a[i];
            }
            out.println(Math.min(solve(n, a), solve(n, b)));
        }

        private int solve(int n, int[] a) {
            int res = 0;
            int bad0 = 0, bad1 = 0;
            for (int i = 0; i < n; i += 2) {
                if (a[i] == 1) {
                    ++bad1;
                }
            }
            for (int i = 1; i < n; i += 2) {
                if (a[i] == 0) {
                    ++bad0;
                }
            }
            int min = Math.min(bad0, bad1);
            bad0 -= min;
            bad1 -= min;
            return min + bad0 + bad1;
        }

    }

    static class InputReader {
        private BufferedReader reader;
        private StringTokenizer stt;

        public InputReader(InputStream stream) {
            reader = new BufferedReader(new InputStreamReader(stream));
        }

        public String nextLine() {
            try {
                return reader.readLine();
            } catch (IOException e) {
                return null;
            }
        }

        public String nextString() {
            while (stt == null || !stt.hasMoreTokens()) {
                stt = new StringTokenizer(nextLine());
            }
            return stt.nextToken();
        }

        public int nextInt() {
            return Integer.parseInt(nextString());
        }

    }
}

